    private void btn_calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_calcularActionPerformed
        double total=0;
        double subtotal=0;
        
        //CALCULANDO OS LANCHES
        if(jcb_xsalada.isSelected()){
            subtotal = subtotal + 12;
        } 
        if(jcb_xveg.isSelected()){
            subtotal = subtotal + 10;
        } 
        if(jcb_xbacon.isSelected()){
            subtotal = subtotal + 15;
        } 
        if(jcb_xmexico.isSelected()){
            subtotal = subtotal + 17;
        }
        //CALCULANDO AS BEBIDAS
        if(jcb_suco.isSelected()){
            subtotal = subtotal + 7;
        }
        if(jcb_refrilit.isSelected()){
            subtotal = subtotal +  8.50;
        } 
        if(jcb_cerveja.isSelected()){
            subtotal = subtotal + 3.50;
        } else{
            total = subtotal;
        }
        
        if(jrb_entrega.isSelected()){
            total = subtotal + 10;
        }else {
            total = subtotal;
        }
        jtf_total.setText(Double.toString(total));
        jtf_subtotal.setText(Double.toString(subtotal));
    }//GEN-LAST:event_btn_calcularActionPerformed

    private void btn_resetarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_resetarActionPerformed
        jtf_total.setText("");
        jtf_subtotal.setText("");
        jcb_cerveja.setSelected(false);
        jcb_xsalada.setSelected(false);
        jcb_xveg.setSelected(false);
        jcb_xbacon.setSelected(false);
        jcb_xmexico.setSelected(false);
        jcb_suco.setSelected(false);
        jcb_refrilit.setSelected(false);
        if(jrb_entrega.isSelected()){
            jrb_entrega.setSelected(false);
        }
        if(jrb_retirada.isSelected()){
            jrb_retirada.setSelected(false);
        }
    }//GEN-LAST:event_btn_resetarActionPerformed

